import MainPage from "./components/main-page.component";
import Menu from "./components/sidebar.component";
import Header from "./components/header.component";
import Footer from "./components/footer.component";
import Dashboard from "./components/dashboard.component";
import React from "react";

import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import { BrowserRouter as Router, Route, Routes, Link, withRouter } from "react-router-dom";
import Loginpage from "./components/login.component";

class App extends React.Component {

  render () {
    return (
    <div class="wrapper">
      <Router>
        {this.props.location.pathname !== '/login' && <Header />}
        {this.props.location.pathname !== '/login' && <Menu />}
        <Routes>
          <Route path="/login" element={<Loginpage />}/>
          <Route path="/" exact element={<Dashboard />}/>
        </Routes>
        {this.props.location.pathname !== '/login' && <Footer />}
      </Router>
    </div>
    );
  }
}

export default withRouterApp;
