import Dashboard from "./components/dashboard.component";

import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Link,
  useLocation
} from "react-router-dom";
import Loginpage from "./components/login.component";

